mapper.select:
SELECT *
  FROM MEMBER
 WHERE ID = #{id}
   AND PW = #{pw}

	SELECT *
	FROM (
	    SELECT ROWNUM RN, T.*
	    FROM (
	        SELECT *
			FROM REVIEW
			WHERE ISBN = #{isbn}
			ORDER BY TO_DATE(DATETIME, 'YYYY-MM-DD') DESC
	    ) T
    WHERE ROWNUM <= (#{cri.pageStart} + #{cri.perPageNum} - 1)
	)
	WHERE RN >= #{cri.pageStart}

SELECT COUNT(*)
  FROM REVIEW
 WHERE ISBN = #{isbn}

 SELECT * FROM (
  SELECT * FROM BOOK WHERE ISBN !=#{isbn} and CATEGORY = #{category} ORDER BY DBMS_RANDOM.RANDOM
) WHERE ROWNUM <= 5
